let quests={quest_list:[{name:"Find my dog!",description:"I lost my dog! can you help me find it? Some monsters took him from me!",goal_item:"Lost Dog",goal_item_description:"You found the lost dog! Take him back to his owner and they will reward you!",completion_message:"Thank you for finding me dog! I was worried sick!"},{name:"Kill a hellhound",type:QUEST_TYPES.KILL,count:1,description:"Kill a single hellhound.",goal_monsters:["Hellhound"],goal_item:null,completion_message:"Thank you for killing the hellhound!"},{name:"Kill 5 hellhounds",type:QUEST_TYPES.KILL,count:5,description:"Kill 5 hellhounds to save the town. Bring back their pelts to make valuable blankets that are always warm like you just got them out of the drier.",goal_monsters:["Hellhound"],goal_item:{name:"Hellhound Pelt",description:"If you return this to the quest giver they can turn this into an awesome forever-warm blanket.",icon:"images/pelt.png"},completion_message:"Thank you for killing the hellhounds! You saved the town, and now we have amazing magical blankets!"},{name:"Talk to the Old Man!",type:QUEST_TYPES.TALK,description:"Send word to Old Man RiverBeard that his grandson has just been born. He needs to know the good news!",goal_npc_name:"Old Man RiverBeard",goal_npc_icon:null,goal_npc_description:"An old man, wise from years of experience, happy and sad time.",completion_message:"Thank you for telling me. It warms my heart to know this world is filled with new life!"},{name:"Find the Dungeon of Canthdor the Wicked",type:QUEST_TYPES.COMPLETE_DUNGEON,description:"A sacred family heirloom of mine was taken by bandits into the dungeon of Canthdor. He is an evil wizard that will buy creatures or even people for his experiments.",goal_dungeon_name:"Dungeon of Canthdor the Wicked",goal_item:"Family Heirloom",completion_message:"Thank you for finding my family's heirloom!"},{name:"Find the map!",description:"An evil wizard rules this land. There is a map to his secret hideaway somewhere in the wilderness. Find it so you can find the wizard.",goal_item:"Wizard Map",goal_item_description:"A map to the wizard. It is written in ancient runes. Go back to the Quest Giver to get it translated.",completion_message:"Thanks for finding the map! It will help us in our quest."},{name:"Find the sword!",description:"The map leads to a sword long buried. It will hold the key to locating the Wizard's portal. Find it so that you may find the Wizard.",goal_item:"Lightly Glowing Sword",goal_item_description:"An ancient sword, no longer usable, but still glows with magic sometimes.",completion_message:"Thanks for finding this important item for locating the wizard!"},{name:"Kill the Wizard!",description:"You can now locate the wizard with the old sword. It will glow when near, allowing you to find the hideout.",goal_item:"Wizard's Head",goal_item_description:"You killed the wizard! Great Job! The land of Dagu is now free once again!",completion_message:"Thank you for saving the realm!"}],getQuest:function(quest_name){for(let i=0;i<quests.quest_list.length;i++){if(quest_name===quests.quest_list[i].name){return quests.quest_list[i]}}return null},getAvailableQuests:function(){let ans=[];for(let i=0;i<QUESTS.SIDE.length;i++){if(!util.isQuestCompleted(QUESTS.SIDE[i].name)){ans.push(QUESTS.SIDE[i].name)}}let story_quest=util.getNextStoryQuest();if(story_quest!==null){ans.push(story_quest)}return ans},getNextStoryQuest:function(){for(let i=0;i<QUESTS.STORY.length;i++){if(!util.isQuestCompleted(QUESTS.STORY[i].name)){return QUESTS.STORY[i].name}}return null},isQuestCompleted:function(quest_name){for(let i=0;i<game.character.completed_quests.length;i++){if(quest_name===game.character.completed_quests[i]){return true}}return false},hasQuestItem:function(quest_name){let quest=util.getQuest(quest_name);for(let i=0;i<game.character.inventory.quest_items.length;i++){if(quest.goal_item===game.character.inventory.quest_items[i]){return true}}return false},hasQuest:function(quest_name){for(let i=0;i<game.character.quests.length;i++){if(game.character.quests[i]===quest_name){return true}}return false},getRandomAvailableQuestName:function(){let quests=util.getAvailableQuests();if(quests.length>0){return util.getRandomItemInArray(quests)}else{return null}},directionTowardGoalItem:function(quest_name){let quest=util.getQuest(quest_name);let points=map.getAll();let goal_items=[];for(let i=0; i<points.length; i++){let map_entry=map.get(points[i].x,points[i].y);if(map_entry.npc!=null&&map_entry.npc.type==="monster"&&map_entry.npc.goal_item===quest.goal_item){goal_items.push(i)}}if(goal_items.length===0){return null}else{let min=util.distanceBetween(game.character.x,game.character.y,points[0].x,points[0].y);let min_i=0;for(let i=1; i<goal_items.length; i++){if(min>util.distanceBetween(game.character.x,game.character.y,points[goal_items[i]].x,points[goal_items[i]].y)){min_i=i;min=util.distanceBetween(game.character.x,game.character.y,points[goal_items[i]].x,points[goal_items[i]].y)}}return util.findDirection(game.character.x,game.character.y,points[goal_items[min_i]].x,points[goal_items[min_i]].y)}},directionTowardQuestNPC:function(quest_name){let quest=util.getQuest(quest_name);let points=map.getAll();let quest_givers=[];for(let i=0;i<points.length;i++){let map_entry=map.get(points[i].x,points[i].y);if(map_entry.npc!=null&&map_entry.npc.type==="quest_giver"&&map_entry.npc.quest===quest.name){quest_givers.push(i)}}if(quest_givers.length===0){return null}else{let min=util.distanceBetween(game.character.x,game.character.y,points[0].x,points[0].y);let min_i=0;for(let i=1;i<quest_givers.length;i++){if(min>util.distanceBetween(game.character.x,game.character.y,points[quest_givers[i]].x,points[quest_givers[i]].y)){min_i=i;min=util.distanceBetween(game.character.x,game.character.y,points[quest_givers[i]].x,points[quest_givers[i]].y)}}return util.findDirection(game.character.x,game.character.y,points[quest_givers[min_i]].x,points[quest_givers[min_i]].y)}},getQuestFromGoalItem:function(goal_item_name){for(let i=0;i<QUESTS.SIDE.length;i++){if(QUESTS.SIDE[i].goal_item===goal_item_name){return QUESTS.SIDE[i]}}for(let i=0;i<QUESTS.STORY.length;i++){if(QUESTS.STORY[i].goal_item===goal_item_name){return QUESTS.STORY[i]}}return null}};